import React from 'react';
import { Link } from 'react-router-dom';

const LandingPage = () => {
  return (
    <div className="min-h-screen relative overflow-hidden">
      {/* Background Image */}
      <div 
        className="absolute inset-0 bg-cover bg-center bg-no-repeat"
        style={{
          backgroundImage: 'url("/landing-background.png")',
        }}
      ></div>

      {/* Header Banner */}
      <div className="relative z-10 backdrop-blur-lg bg-gradient-to-r from-slate-800/60 via-blue-900/50 to-teal-800/60 border-b border-white/20 shadow-lg">
        <header className="flex justify-between items-center p-6 max-w-7xl mx-auto">
          {/* Logo */}
          <div className="flex items-center">
            <img 
              src="/title-logo.png" 
              alt="Crestview Eighty Logo" 
              className="h-6 mr-3 object-contain bg-transparent"
              style={{ backgroundColor: 'transparent' }}
            />
            <h1 className="text-xl font-bold text-white drop-shadow-sm">CRESTVIEW EIGHTY</h1>
          </div>

          {/* Navigation */}
          <nav className="flex gap-6">
            <Link 
              to="/map" 
              className="text-white/90 hover:text-white hover:bg-white/10 px-4 py-2 rounded-lg font-medium text-sm uppercase tracking-wide transition-all duration-200 backdrop-blur-sm"
            >
              map
            </Link>
            <Link 
              to="/contact" 
              className="text-white/90 hover:text-white hover:bg-white/10 px-4 py-2 rounded-lg font-medium text-sm uppercase tracking-wide transition-all duration-200 backdrop-blur-sm"
            >
              contact
            </Link>
            <Link 
              to="/portal" 
              className="text-white/90 hover:text-white hover:bg-white/10 px-4 py-2 rounded-lg font-medium text-sm uppercase tracking-wide transition-all duration-200 backdrop-blur-sm"
            >
              login
            </Link>
          </nav>
        </header>
      </div>

      {/* Main Content */}
      <div className="relative z-10 flex flex-col items-center justify-center min-h-screen -mt-16">
        {/* Central Login Button */}
        <Link 
          to="/portal" 
          className="group relative backdrop-blur-lg bg-gradient-to-r from-blue-600/80 to-teal-600/80 hover:from-blue-700/90 hover:to-teal-700/90 text-white font-bold py-6 px-16 rounded-2xl transition-all duration-300 transform hover:scale-105 hover:shadow-2xl border border-white/20 shadow-xl text-xl"
        >
          <div className="absolute inset-0 bg-gradient-to-r from-blue-500/20 to-teal-500/20 rounded-2xl blur-xl group-hover:blur-2xl transition-all duration-300"></div>
          <span className="relative">Login</span>
        </Link>
      </div>
    </div>
  );
};

export default LandingPage;
