import React, { useState } from 'react';
import { 
  Users, 
  UserPlus, 
  Edit, 
  Trash2, 
  Search, 
  Filter,
  Shield,
  User,
  Crown,
  X,
  Mail,
  Phone,
  Home,
  Calendar,
  Key,
  Eye,
  EyeOff
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

const UserManagement = () => {
  const [users, setUsers] = useState([
    {
      id: '1',
      name: 'HOA Administrator',
      email: 'admin@crestvieweighty.com',
      role: 'admin',
      address: '123 Crestview Dr',
      phone: '(406) 555-0101',
      status: 'active',
      joinDate: '2023-01-15'
    },
    {
      id: '2',
      name: 'John Smith',
      email: 'board@crestvieweighty.com',
      role: 'board',
      address: '456 Mountain View Ln',
      phone: '(406) 555-0102',
      status: 'active',
      joinDate: '2023-02-20'
    },
    {
      id: '3',
      name: 'Jane Doe',
      email: 'member@crestvieweighty.com',
      role: 'member',
      address: '789 Lake Vista Ct',
      phone: '(406) 555-0103',
      status: 'active',
      joinDate: '2023-03-10'
    },
    {
      id: '4',
      name: 'Mike Johnson',
      email: 'mike.johnson@email.com',
      role: 'member',
      address: '321 Pine Ridge Ave',
      phone: '(406) 555-0104',
      status: 'inactive',
      joinDate: '2023-04-05'
    }
  ]);

  const [searchTerm, setSearchTerm] = useState('');
  const [filterRole, setFilterRole] = useState('all');
  const [showAddUser, setShowAddUser] = useState(false);
  const [showEditUser, setShowEditUser] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showPassword, setShowPassword] = useState(false);

  // New user form state
  const [newUser, setNewUser] = useState({
    name: '',
    email: '',
    password: '',
    role: 'member',
    address: '',
    phone: '',
    unitNumber: '',
    emergencyContact: '',
    emergencyPhone: ''
  });

  const roleConfig = {
    admin: { 
      icon: Shield, 
      label: 'Administrator', 
      color: 'red',
      gradient: 'from-red-500 to-pink-500',
      bgGradient: 'from-red-500/10 to-pink-500/10',
      description: 'Full system access and management capabilities'
    },
    board: { 
      icon: Crown, 
      label: 'Board Member', 
      color: 'blue',
      gradient: 'from-blue-500 to-indigo-500',
      bgGradient: 'from-blue-500/10 to-indigo-500/10',
      description: 'Enhanced access with board-level permissions'
    },
    member: { 
      icon: User, 
      label: 'Member', 
      color: 'green',
      gradient: 'from-green-500 to-emerald-500',
      bgGradient: 'from-green-500/10 to-emerald-500/10',
      description: 'Standard member access to community resources'
    }
  };

  const filteredUsers = users.filter(user => {
    const matchesSearch = user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         user.email.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesRole = filterRole === 'all' || user.role === filterRole;
    return matchesSearch && matchesRole;
  });

  const handleAddUser = (e) => {
    e.preventDefault();
    const newUserId = (Math.max(...users.map(u => parseInt(u.id))) + 1).toString();
    const userToAdd = {
      ...newUser,
      id: newUserId,
      status: 'active',
      joinDate: new Date().toISOString().split('T')[0]
    };
    setUsers([...users, userToAdd]);
    setNewUser({
      name: '',
      email: '',
      password: '',
      role: 'member',
      address: '',
      phone: '',
      unitNumber: '',
      emergencyContact: '',
      emergencyPhone: ''
    });
    setShowAddUser(false);
  };

  const handleEditUser = (user) => {
    setSelectedUser(user);
    setShowEditUser(true);
  };

  const handleDeleteUser = (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      setUsers(users.filter(u => u.id !== userId));
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h2 className="text-2xl font-bold text-white flex items-center gap-2">
            <Users className="w-6 h-6" />
            User Management
          </h2>
          <p className="text-blue-200">Manage community members and access permissions</p>
        </div>

        <button
          onClick={() => setShowAddUser(true)}
          className="flex items-center gap-2 px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg transition-colors"
        >
          <UserPlus className="w-4 h-4" />
          Add User
        </button>
      </div>

      {/* Filters */}
      <div className="backdrop-blur-lg bg-white/10 rounded-xl p-6 border border-white/20">
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search users..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-2 bg-white/10 border border-white/20 rounded-lg text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
          </div>

          <div className="relative">
            <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
            <select
              value={filterRole}
              onChange={(e) => setFilterRole(e.target.value)}
              className="pl-10 pr-8 py-2 bg-white/10 border border-white/20 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-blue-400"
            >
              <option value="all">All Roles</option>
              <option value="admin">Administrators</option>
              <option value="board">Board Members</option>
              <option value="member">Members</option>
            </select>
          </div>
        </div>
      </div>

      {/* Users Table */}
      <div className="backdrop-blur-lg bg-white/10 rounded-xl border border-white/20 overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-white/10">
              <tr>
                <th className="text-left px-6 py-4 text-white font-medium">User</th>
                <th className="text-left px-6 py-4 text-white font-medium">Role</th>
                <th className="text-left px-6 py-4 text-white font-medium">Contact</th>
                <th className="text-left px-6 py-4 text-white font-medium">Status</th>
                <th className="text-left px-6 py-4 text-white font-medium">Actions</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-white/10">
              {filteredUsers.map((user) => {
                const RoleIcon = roleConfig[user.role].icon;
                return (
                  <tr key={user.id} className="hover:bg-white/5 transition-colors">
                    <td className="px-6 py-4">
                      <div>
                        <div className="text-white font-medium">{user.name}</div>
                        <div className="text-blue-200 text-sm">{user.email}</div>
                        <div className="text-blue-300 text-sm">{user.address}</div>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className={`flex items-center gap-2 px-3 py-1 bg-${roleConfig[user.role].color}-500/20 rounded-full w-fit`}>
                        <RoleIcon className={`w-4 h-4 text-${roleConfig[user.role].color}-300`} />
                        <span className={`text-${roleConfig[user.role].color}-300 text-sm`}>
                          {roleConfig[user.role].label}
                        </span>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="text-blue-200 text-sm">{user.phone}</div>
                      <div className="text-blue-300 text-xs">Joined: {user.joinDate}</div>
                    </td>
                    <td className="px-6 py-4">
                      <span className={`px-2 py-1 rounded-full text-xs ${
                        user.status === 'active' 
                          ? 'bg-green-500/20 text-green-300' 
                          : 'bg-red-500/20 text-red-300'
                      }`}>
                        {user.status}
                      </span>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex items-center gap-2">
                        <button className="p-2 text-blue-300 hover:text-white hover:bg-white/10 rounded-lg transition-colors">
                          <Edit className="w-4 h-4" />
                        </button>
                        <button className="p-2 text-red-300 hover:text-white hover:bg-red-500/20 rounded-lg transition-colors">
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="backdrop-blur-lg bg-white/10 rounded-xl p-4 border border-white/20 text-center">
          <div className="text-2xl font-bold text-white">{users.length}</div>
          <div className="text-blue-200 text-sm">Total Users</div>
        </div>
        <div className="backdrop-blur-lg bg-white/10 rounded-xl p-4 border border-white/20 text-center">
          <div className="text-2xl font-bold text-white">{users.filter(u => u.status === 'active').length}</div>
          <div className="text-blue-200 text-sm">Active Users</div>
        </div>
        <div className="backdrop-blur-lg bg-white/10 rounded-xl p-4 border border-white/20 text-center">
          <div className="text-2xl font-bold text-white">{users.filter(u => u.role === 'admin').length}</div>
          <div className="text-blue-200 text-sm">Administrators</div>
        </div>
        <div className="backdrop-blur-lg bg-white/10 rounded-xl p-4 border border-white/20 text-center">
          <div className="text-2xl font-bold text-white">{users.filter(u => u.role === 'member').length}</div>
          <div className="text-blue-200 text-sm">Members</div>
        </div>
      </div>

      {/* Add User Modal would go here */}
      {showAddUser && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
          <div className="backdrop-blur-lg bg-white/10 rounded-xl p-6 border border-white/20 max-w-md w-full">
            <h3 className="text-lg font-semibold text-white mb-4">Add New User</h3>
            <p className="text-blue-200 mb-4">User creation form coming soon...</p>
            <button
              onClick={() => setShowAddUser(false)}
              className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserManagement;
