import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { X, User, Lock } from 'lucide-react';

const LandingPage = () => {
  const [showLoginForm, setShowLoginForm] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    if (username === 'admin' && password === 'admin') {
      navigate('/portal');
    } else {
      setError('Invalid credentials. Please try again.');
    }
  };

  const handleLoginClick = (e) => {
    e.preventDefault();
    setShowLoginForm(true);
  };

  const closeLoginForm = () => {
    setShowLoginForm(false);
    setUsername('');
    setPassword('');
    setError('');
  };

  return (
    <div className="min-h-screen relative overflow-hidden">
      {/* Background Image */}
      <div 
        className="absolute inset-0 bg-cover bg-center bg-no-repeat"
        style={{
          backgroundImage: 'url("/landing-background.png")',
        }}
      ></div>

      {/* Header Banner */}
      <div className="relative z-10 backdrop-blur-xl bg-gradient-to-r from-white/85 via-gray-50/80 to-white/85 border-b border-gray-200/30 shadow-xl">
        <header className="flex justify-between items-center p-6 max-w-7xl mx-auto">
          {/* Logo */}
          <div className="flex items-center">
            <img 
              src="/title-logo.png" 
              alt="Crestview Eighty Logo" 
              className="h-12 mr-4 object-contain bg-transparent"
              style={{ backgroundColor: 'transparent' }}
            />
            <h1 className="text-2xl font-bold text-slate-800 drop-shadow-sm">CRESTVIEW EIGHTY</h1>
          </div>

          {/* Navigation */}
          <nav className="flex gap-2">
            <Link 
              to="/map" 
              className="bg-white text-white bg-gradient-to-r from-blue-600 to-teal-600 hover:from-blue-700 hover:to-teal-700 px-6 py-3 rounded-xl font-semibold text-sm tracking-wider transition-all duration-300 shadow-md hover:shadow-lg transform hover:scale-105 border border-white/20"
              style={{ fontFamily: 'system-ui, -apple-system, sans-serif', letterSpacing: '0.05em' }}
            >
              MAP
            </Link>
            <Link 
              to="/contact" 
              className="bg-white text-white bg-gradient-to-r from-blue-600 to-teal-600 hover:from-blue-700 hover:to-teal-700 px-6 py-3 rounded-xl font-semibold text-sm tracking-wider transition-all duration-300 shadow-md hover:shadow-lg transform hover:scale-105 border border-white/20"
              style={{ fontFamily: 'system-ui, -apple-system, sans-serif', letterSpacing: '0.05em' }}
            >
              CONTACT
            </Link>
            <Link 
              to="/portal" 
              className="bg-white text-white bg-gradient-to-r from-blue-600 to-teal-600 hover:from-blue-700 hover:to-teal-700 px-6 py-3 rounded-xl font-semibold text-sm tracking-wider transition-all duration-300 shadow-md hover:shadow-lg transform hover:scale-105 border border-white/20"
              style={{ fontFamily: 'system-ui, -apple-system, sans-serif', letterSpacing: '0.05em' }}
            >
              LOGIN
            </Link>
          </nav>
        </header>
      </div>

      {/* Main Content */}
      <div className="relative z-10 flex flex-col items-center justify-center min-h-screen -mt-16">
        {/* Central Login Button / Form */}
        <div className="relative">
          {!showLoginForm ? (
            <button
              onClick={handleLoginClick}
              className="group relative backdrop-blur-lg bg-gradient-to-r from-blue-600/80 to-teal-600/80 hover:from-blue-700/90 hover:to-teal-700/90 text-white font-bold py-6 px-16 rounded-2xl transition-all duration-300 transform hover:scale-105 hover:shadow-2xl border border-white/20 shadow-xl text-xl"
            >
              <div className="absolute inset-0 bg-gradient-to-r from-blue-500/20 to-teal-500/20 rounded-2xl blur-xl group-hover:blur-2xl transition-all duration-300"></div>
              <span className="relative">Login</span>
            </button>
          ) : (
            <div 
              className={`backdrop-blur-xl bg-white/95 rounded-2xl border border-white/30 shadow-2xl p-8 w-96 transform transition-all duration-500 ease-out ${
                showLoginForm ? 'scale-100 opacity-100' : 'scale-75 opacity-0'
              }`}
            >
              {/* Close Button */}
              <button
                onClick={closeLoginForm}
                className="absolute top-4 right-4 text-gray-500 hover:text-gray-700 transition-colors"
              >
                <X className="w-6 h-6" />
              </button>

              {/* Header */}
              <div className="text-center mb-6">
                <h2 className="text-2xl font-bold text-gray-800 mb-2">Member Portal</h2>
                <p className="text-gray-600">Sign in to access your account</p>
              </div>

              {/* Login Form */}
              <form onSubmit={handleLogin} className="space-y-6">
                {/* Username Field */}
                <div className="relative">
                  <User className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
                  <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    className="w-full pl-10 pr-4 py-3 border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500/20 focus:border-blue-500 outline-none transition-all backdrop-blur-sm bg-white/80"
                    required
                  />
                </div>

                {/* Password Field */}
                <div className="relative">
                  <Lock className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
                  <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="w-full pl-10 pr-4 py-3 border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500/20 focus:border-blue-500 outline-none transition-all backdrop-blur-sm bg-white/80"
                    required
                  />
                </div>

                {/* Error Message */}
                {error && (
                  <div className="text-red-600 text-sm text-center bg-red-50 py-2 px-4 rounded-lg">
                    {error}
                  </div>
                )}

                {/* Login Button */}
                <button
                  type="submit"
                  className="w-full bg-gradient-to-r from-blue-600 to-teal-600 hover:from-blue-700 hover:to-teal-700 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl"
                >
                  Sign In
                </button>

                {/* Demo Credentials */}
                <div className="text-center text-xs text-gray-500 bg-gray-50/80 py-2 px-4 rounded-lg">
                  Demo: admin / admin
                </div>
              </form>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
