import React, { useState } from 'react';
import { 
  Users, 
  FileText, 
  Settings, 
  BarChart3, 
  Calendar, 
  CreditCard,
  Bell,
  Shield,
  Database,
  Edit3
} from 'lucide-react';
import PortalLayout from './PortalLayout';
import UserManagement from '../admin/UserManagement';
import DocumentManagement from '../admin/DocumentManagement';
import ContentManager from '../admin/ContentManager';

const AdminPortal = () => {
  const [activeTab, setActiveTab] = useState('dashboard');

  const adminTabs = [
    { id: 'dashboard', label: 'Dashboard', icon: BarChart3 },
    { id: 'users', label: 'User Management', icon: Users },
    { id: 'documents', label: 'Documents', icon: FileText },
    { id: 'content', label: 'Content Manager', icon: Edit3 },
    { id: 'calendar', label: 'Events', icon: Calendar },
    { id: 'payments', label: 'Payments', icon: CreditCard },
    { id: 'notifications', label: 'Notifications', icon: Bell },
    { id: 'settings', label: 'Settings', icon: Settings }
  ];

  const renderTabContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return <AdminDashboard />;
      case 'users':
        return <UserManagement />;
      case 'documents':
        return <DocumentManagement />;
      case 'content':
        return <ContentManager />;
      case 'calendar':
        return <EventManagement />;
      case 'payments':
        return <PaymentManagement />;
      case 'notifications':
        return <NotificationCenter />;
      case 'settings':
        return <SystemSettings />;
      default:
        return <AdminDashboard />;
    }
  };

  return (
    <PortalLayout
      title="Admin Portal"
      subtitle="Full system administration and management"
      tabs={adminTabs}
      activeTab={activeTab}
      onTabChange={setActiveTab}
      userRole="admin"
    >
      {renderTabContent()}
    </PortalLayout>
  );
};

const AdminDashboard = () => {
  const stats = [
    { label: 'Total Members', value: '127', change: '+3', icon: Users, color: 'blue' },
    { label: 'Pending Payments', value: '$12,450', change: '-5%', icon: CreditCard, color: 'green' },
    { label: 'Active Documents', value: '45', change: '+2', icon: FileText, color: 'purple' },
    { label: 'System Health', value: '98.5%', change: '+0.2%', icon: Shield, color: 'emerald' }
  ];

  return (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat, index) => (
          <div key={index} className="backdrop-blur-lg bg-white/10 rounded-xl p-6 border border-white/20">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-blue-200 text-sm">{stat.label}</p>
                <p className="text-2xl font-bold text-white">{stat.value}</p>
                <p className={`text-sm ${stat.change.startsWith('+') ? 'text-green-400' : 'text-red-400'}`}>
                  {stat.change} from last month
                </p>
              </div>
              <div className={`p-3 bg-${stat.color}-500/20 rounded-lg`}>
                <stat.icon className={`w-6 h-6 text-${stat.color}-300`} />
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="backdrop-blur-lg bg-white/10 rounded-xl p-6 border border-white/20">
          <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
            <Bell className="w-5 h-5" />
            Recent Activity
          </h3>
          <div className="space-y-3">
            {[
              'New member registration: Sarah Johnson',
              'Document uploaded: 2024 Budget Proposal',
              'Payment received: Unit 47 - March dues',
              'System backup completed successfully',
              'Board meeting scheduled: March 15th'
            ].map((activity, index) => (
              <div key={index} className="text-blue-200 text-sm p-3 bg-white/5 rounded-lg">
                {activity}
              </div>
            ))}
          </div>
        </div>

        <div className="backdrop-blur-lg bg-white/10 rounded-xl p-6 border border-white/20">
          <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
            <Database className="w-5 h-5" />
            System Status
          </h3>
          <div className="space-y-4">
            {[
              { service: 'Web Server', status: 'Online', uptime: '99.9%' },
              { service: 'Database', status: 'Online', uptime: '99.8%' },
              { service: 'Payment Gateway', status: 'Online', uptime: '99.5%' },
              { service: 'Email Service', status: 'Online', uptime: '98.2%' }
            ].map((service, index) => (
              <div key={index} className="flex justify-between items-center">
                <span className="text-blue-200">{service.service}</span>
                <div className="flex items-center gap-2">
                  <span className="text-green-400 text-sm">{service.status}</span>
                  <span className="text-blue-300 text-sm">{service.uptime}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

const EventManagement = () => (
  <div className="backdrop-blur-lg bg-white/10 rounded-xl p-6 border border-white/20">
    <h3 className="text-lg font-semibold text-white mb-4">Event Management</h3>
    <p className="text-blue-200">Event calendar management features coming soon...</p>
  </div>
);

const PaymentManagement = () => (
  <div className="backdrop-blur-lg bg-white/10 rounded-xl p-6 border border-white/20">
    <h3 className="text-lg font-semibold text-white mb-4">Payment Management</h3>
    <p className="text-blue-200">Payment processing and dues management features coming soon...</p>
  </div>
);

const NotificationCenter = () => (
  <div className="backdrop-blur-lg bg-white/10 rounded-xl p-6 border border-white/20">
    <h3 className="text-lg font-semibold text-white mb-4">Notification Center</h3>
    <p className="text-blue-200">Mass notification and communication features coming soon...</p>
  </div>
);

const SystemSettings = () => (
  <div className="backdrop-blur-lg bg-white/10 rounded-xl p-6 border border-white/20">
    <h3 className="text-lg font-semibold text-white mb-4">System Settings</h3>
    <p className="text-blue-200">System configuration and settings panel coming soon...</p>
  </div>
);

export default AdminPortal;
